import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import './Expenses.css';
import ExpensesFilter from './ExpensesFilter';
import { useState } from 'react';

const Expenses = (props) => {
    const [numbers, setNumbers] = useState([]);

    const [filteredYear, setFilteredYear] = useState('2020');

    const callExpensesFilter = (value) => {
        setFilteredYear(value);
        //console.log(props.items.map((num) => num.title));
        const temp = [];
        for (let i = 0; i < props.items.title.length; i++) {
            //console.log(value);
            //console.log(props.items[i].date.getFullYear());
            //console.log(value == props.items[i].date.getFullYear());
            if (value == props.items[i].date.getFullYear()) {
                temp.push(i);
            }
            setNumbers([...temp]);
        }
    };
    //console.log(numbers);
    //console.log(numbers.map((num) => props.items[num].title));
    return (
        <Card className="expenses">
            <ExpensesFilter
                selected={filteredYear}
                onExpensesFilter={callExpensesFilter}
            ></ExpensesFilter>
            {/* for(let i = 0; i < numbers.length; i++) */}
            {numbers.map((num) => (
                <ExpenseItem
                    key={props.items[num].id}
                    title={props.items[num].title}
                    amount={props.items[num].amount}
                    date={props.items[num].date}
                />
            ))}
        </Card>
    );
};

export default Expenses;
